var updateMenuJSON = false;
var updateWayfinderJSON = false;
var fireCheckEvent = false;

function assembleTree(parentNode,dataArr){
    var c;
    for(c=0;c<dataArr.length;c++){
        newNode = parentNode.appendChild({"text":dataArr[c].text,"expandable":true,"expanded":true,"leaf":true,"isMegaTitle":dataArr[c].isMegaTitle});
        if(dataArr[c].children.length > 0){
            assembleTree(newNode,dataArr[c].children); 
        }
	}
}

function getJson(treeNode) {
    treeNode.expandChildNodes();
    var json = {};
    var attributes = treeNode.attributes;
    for(var item in attributes){
        if (item == 'src' || item == 'text' || item == "isMegaTitle") {
            json[item] = attributes[item];
        }
	}
    json.children = [];
    if(treeNode.childNodes.length > 0){
        for (var i=0; i < treeNode.childNodes.length; i++) {
            json.children.push(getJson(treeNode.childNodes[i]));
        }
    }
    return json;
}

function getMenuStyle(_elem, _style){
    var computedStyle;
    if (_elem != 'undefined' && (typeof _elem.currentStyle != 'undefined'))
    { computedStyle = _elem.currentStyle; }
    else
    { computedStyle = document.defaultView.getComputedStyle(_elem, null); }
    return computedStyle[_style];
} 

