jQuery(document).ready(function() {
        jQuery("#skip_to_content").click(function(e) {
            if (!jQuery("#skip_to_content_inner").length) {
                jQuery(".mt_mainContent").eq(0).prepend('<a href="" id="skip_to_content_inner" alt="You are now in the main content area" style="position:absolute;left:-10000px;top:auto;width:1px;height:1px;overflow:hidden;">You are now in the main content area</a>');
                jQuery("#skip_to_content_inner").click(function(e) {
                    e.preventDefault();
                });
            }
            jQuery("#skip_to_content_inner").focus();
            e.preventDefault();
        });

    	setInterval(function(){
	    	jQuery(".cq-editrollover-insert-message").each(function(index,value){
	        var itemWidth = jQuery(value).parent().width();
	        if(itemWidth <= 45){
                jQuery(value).css("font-size","0px");
            }else if(itemWidth <= 50){
                jQuery(value).css("font-size","6px");
            } else if(itemWidth <= 55){ 
                jQuery(value).css("font-size","7px");
            } else if(itemWidth <= 70){  
                jQuery(value).css("font-size","8px"); 
            } else if(itemWidth <= 85){
                jQuery(value).css("font-size","10px");
            } else if(itemWidth <= 105){
                jQuery(value).css("font-size","12px");
            } else if(itemWidth <= 125){
                jQuery(value).css("font-size","13px");
            } else{
                jQuery(value).css("font-size","16px");
            }
            });
	    
	},1500);
});
