jQuery(document).ready(function(){
    jQuery("form.resSearch input[name='q']").attr('autocomplete', 'off').typeahead({
        minLength: 3,
        delay: 200,
        autoSelect: false,
        fitToElement: false,
        afterSelect: resSearchAutoCompleteSelected,
        source: function(q, process) {
            return jQuery.get('/rye-api/autocomplete', { q: q }, function (data) {
                return process(parseGoogleAutocompleteData(data));
            });
        }
    }).change(function() {
      var current = jQuery(this).typeahead("getActive");
      if (current) {
          if (current.link) {
              jQuery("form.resSearch").off('submit');
              location.href = current.link;
              jQuery(this).val('');
          }
      }
    });
});

