function addPageFields(itemDomNode, item, settings) {

	if(settings.displayDate != null){
		var date = document.createElement("div");
		itemDomNode.appendChild(date);
		date.className += " pageDate";
		if (settings.displayDate == "jcr:lastReplicated") {
			date.innerHTML = jQuery.format.date(new Date(item.replicated).getTime(),
					"MMMM d, yyyy");
		} else if (settings.displayDate == "cq:lastModified") {
			date.innerHTML = jQuery.format.date(new Date(item.modified).getTime(),
					"MMMM d, yyyy");
		} else if (settings.displayDate == "jcr:created") {
			date.innerHTML = jQuery.format.date(new Date(item.created).getTime(),
					"MMMM d, yyyy");
		}
	}

	if (settings.displayTitle && item.title != null) {
        var titleContainer = document.createElement("div");
		titleContainer.className += "pageTitle";
		itemDomNode.appendChild(titleContainer);

		var title = document.createElement("a");
		titleContainer.appendChild(title);
		title.innerHTML = htmlDecode(item.title);
		title.setAttribute('href', getEnvRelativeURL(item.page, runMode));
		if(settings.displayAs == "grid") {
			title.setAttribute('aria-hidden', "true");
		}
	}

	if (settings.displayMetaDesc && item.metaDescription != null) {
		var desc = document.createElement("div");
		itemDomNode.appendChild(desc);
		desc.className += " pageMetaDescription";
		desc.innerHTML = item.metaDescription;
	}

	if (settings.displayTags && item.tags != null) {
		var tags = document.createElement("div");
		itemDomNode.appendChild(tags);
		tags.className += " pageTags";
        tags.innerHTML = "Tags: ";
		for (var i = 0; i < item.tags.length; i++) {
            if(i > 0){
				tags.innerHTML += ", ";
            }
			var tag = document.createElement("span");
			tags.appendChild(tag);
			tag.className += " tag";
			tag.innerHTML = item.tags[i];
		}
	}

    if (item.customFields != null) {
        for(var propertyName in item.customFields){
			var div = document.createElement("div");
            itemDomNode.appendChild(div);
            div.className += " "+propertyName;
            div.innerHTML = htmlDecode(item.customFields[propertyName]);
        }
	}
}
